import React, { Component } from "react";

class CreditTable extends Component {
  constructor(props) {
    super(props);

    this.state = {
      cards: [
        { id: 1, name: "Wells Fargo", balance: 1000, limit: 9500, apr: 25.66 }
      ]
    };
  }

  renderTableData() {
    return this.state.cards.map((card, index) => {
      const { id, name, age, email } = card; //destructuring
      return (
        <tr key={id}>
          <td>{id}</td>
          <td>{name}</td>
          <td>${balance}</td>
          <td>${limit}</td>
          <td>{apr}%</td>
        </tr>
      );
    });
  }

  render() {
    return (
      <div className="creditTable">
        <h1>Credit Table</h1>

        <table id="students">
          <tbody>{this.renderTableData()}</tbody>
        </table>
      </div>
    );
  }
}

export default CreditTable;
