import React, { Component } from "react";

class CreditTable extends Component {
  constructor(props) {
    super(props);

    this.state = {
      cards: [{ id: 1, name: "Wells Fargo", age: 21, email: "wasif@email.com" }]
    };
  }

  renderTableData() {
    return this.state.cards.map((card, index) => {
      const { id, name, age, email } = card; //destructuring
      return (
        <tr key={id}>
          <td>{id}</td>
          <td>{name}</td>
          <td>{age}</td>
          <td>{email}</td>
        </tr>
      );
    });
  }

  render() {
    return (
      <div className="creditTable">
        <h1>Credit Table</h1>

        <table id="students">
          <tbody>{this.renderTableData()}</tbody>
        </table>
      </div>
    );
  }
}

export default CreditTable;
