import React, { Component } from "react";

const CARD_DRAFT = {
  name: "",
  balance: "",
  limit: "",
  apr: ""
};

class CreditTable extends Component {
  constructor(props) {
    super(props);

    this.state = {
      cards: [
        { id: 1, name: "Wells Fargo", balance: 9049, limit: 9500, apr: 16.99 },
        { id: 2, name: "Chase", balance: 6656, limit: 6700, apr: 25.66 },
        { id: 3, name: "Apple", balance: 1723, limit: 4500, apr: 23.49 },
        { id: 4, name: "Best Buy", balance: 3912, limit: 4000, apr: 26.74 }
      ],
      editMode: false,
      ...CARD_DRAFT
    };
  }

  onChange = event => {
    this.setState({ [event.target.name]: event.target.value });
  };

  onToggleEditMode = () => {
    this.setState(state => ({
      editMode: !state.editMode
    }));
  };

  addNewCard = () => {
    this.setState({ editMode: false });
  };

  renderTableData() {
    return this.state.cards.map((card, index) => {
      const { id, name, balance, limit, apr } = card; //destructuring
      const percentUsed = (balance / limit) * 100;
      const interestMonth = (balance * apr) / 100 / 12;
      const minPayment = interestMonth + balance * 0.01;

      return (
        <tr key={id}>
          <td>{id}</td>
          <td>{name}</td>
          <td>${balance}</td>
          <td>${limit}</td>
          <td>{percentUsed.toFixed(2)}%</td>
          <td>{apr}%</td>
          <td>${interestMonth.toFixed(2)}</td>
          <td>${minPayment.toFixed(2)}</td>
        </tr>
      );
    });
  }

  render() {
    const { editMode } = this.state;

    return (
      <div className="creditTable">
        <h1>Credit Table</h1>

        <table id="students">
          <tbody>
            <Titles />
            {this.renderTableData()}
          </tbody>
        </table>

        {editMode ? (
          <div>
            <form onSubmit={this.onSubmit}>
              <input
                name="id"
                // value={}
                onChange={this.onChange}
                type="text"
                placeholder="Card Name"
              />
              <input
                name="balance"
                // value={}
                onChange={this.onChange}
                type="text"
                placeholder="Balance"
              />
              <input
                name="limit"
                // value={}
                onChange={this.onChange}
                type="text"
                placeholder="Card Limit"
              />
              <input
                name="apr"
                // value={}
                onChange={this.onChange}
                type="text"
                placeholder="Card APR"
              />
            </form>

            <button onClick={this.addNewCard}>Done</button>
          </div>
        ) : (
          <button onClick={this.onToggleEditMode}>Add Another Card</button>
        )}
      </div>
    );
  }
}

const Titles = () => (
  <tr>
    <td>ID</td>
    <td>Name</td>
    <td>Balance</td>
    <td>Limit</td>
    <td>% Used</td>
    <td>APR</td>
    <td>Interest (Month)</td>
    <td>Minimum Payment</td>
  </tr>
);

export default CreditTable;
